module.exports = function (x, y, size, texture, options) {
	this.size = size
	this.texture = texture
	this.options = options

	this.sprite = undefined

	if (this.options && this.options.source !== undefined) {
		this.sprite = Resources.getSprite(this.options.source, this.texture)
	} else {
		this.sprite = Resources.getSprite(this.texture)
	}

	this.sprite.position.x = x * this.size
	this.sprite.position.y = y * this.size
	this.sprite.width = size
	this.sprite.height = size

	this.getPosition = function () {
		return {
			x: Math.floor(this.sprite.position.x / this.size),
			y: Math.floor(this.sprite.position.y / this.size)
		}
	}

	this.setPosition = function (x, y) {
		this.sprite.x = x * this.size
		this.sprite.y = y * this.size
	}

	this.addToStage = function (stage) {
		this.stage = stage
		this.stage.addChild(this.sprite)
	}

	this.removeFromStage = function () {
		this.stage.removeChild(this.sprite)
		this.stage = undefined
	}
}